import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';
import ArrayMessage from './arrayMessage.js';
import SendMessage from './sendMessage.js';
import womIcon from './womIcon.svg';
import manIcon from './manIcon.svg';


function App() {
  const[arrayDb,setArrayDb]= useState([]);
  const[user,setUser]= useState({user:'', reg:true});

  async function fetchGet(){
    const response = await fetch('http://localhost:5000');
    const body = await response.json();
    if (response.status !== 200) {
      throw Error(body.message)
    }
    return body
  }

  useEffect(() => {
    fetchGet().then(function(value){
      setArrayDb(value)
    })
  },[]);

  function handlerUser(e){
    setUser({user:e.target.textContent, reg:false})
  }

  if(user.reg){
    return( 
      <div className='user-change'>
        <h2>Кто ты?</h2>
        <ul>
          <li onClick={handlerUser}>Дима</li>
          <li onClick={handlerUser}>Даша</li>
        </ul>
    </div>)
  }else{
    return (
      <div className='App'>
        <div className="App__header">
          <p className='App__header-title'>{user.user === "Дима" ? 'чат с Дашей' : 'чат с Димой'}</p>
          <div className='App-header-name'>
            <p>Привет, {user.user}</p>
            <img src={user.user === "Дима" ? manIcon : womIcon} />
          </div>

        </div>
        <ArrayMessage user={user.user} arrayDb={arrayDb} setArrayDb={setArrayDb} />
        <SendMessage user={user.user} arrayDb={arrayDb} setArrayDb={setArrayDb} />
      </div>
    );
  }
}

export default App;
