import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
let controller = new AbortController();
let signal = controller.signal;

// срабатывает при вызове controller.abort()
 // отмена!

alert(signal.aborted)
async function ger(){
        const response = await fetch('http://localhost:5000',{signal: controller.signal});
        const body = await response.json();
        if (response.status !== 200) {
          throw Error(body.message)
        }
        return body
    }


function App() {
   const[gert,setGert]= useState([]);
  const AllLists = gert.map((item)=>
    <li>{item.name}</li>
  )


 ger().then(function(value){
  setGert(value)
 signal.addEventListener('abort', () => alert("отмена!"));

controller.abort();
})

  return (
    <div >
      <ul>{AllLists}</ul>
    </div>
  );
}

export default App;
