import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';
import ArrayMessage from './arrayMessage.js';
import SendMessage from './sendMessage.js';



function App() {
  const[arrayDb,setArrayDb]= useState([]);
  const[array,setArray]= useState([]);
  let user={user:'', reg:false}

async function fetchGet(){
        const response = await fetch('http://localhost:5000');
        const body = await response.json();
        if (response.status !== 200) {
          throw Error(body.message)
        }

        return body
    }


  useEffect(() => {
    fetchGet().then(function(value){
      setArrayDb(value)
    })
  });


  function handlerUser(e){
    user = {user:e.target.value, reg:true}
  }

  if(user.reg){
    return( 
      <div className='user-change'>
        <h2>Кто ты?</h2>
        <ul>
          <li onClick={handlerUser}>Дима</li>
          <li onClick={handlerUser}>Даша</li>
        </ul>
    </div>)
  }else{

  return (
    <div >
      <ArrayMessage arrayDb={arrayDb} setArrayDb={setArrayDb} />
      <SendMessage array={array} setArray={setArray} arrayDb={arrayDb} setArrayDb={setArrayDb} />
    </div>
  );}
}

export default App;
