import React, { useState, useEffect } from 'react';

function SendMessage(props){
	const setArrayDb= props.setArrayDb;
	const array = props.arrayDb;
	const [message,setMessage]= useState('');
	function targetMessage(e){
		setMessage(e.target.value);

	}

function hSend(){

 if(new Date().getMinutes() > 9){
 	const time = new Date().getHours() +':'+ new Date().getMinutes()
 }else{
	const time = new Date().getHours() +':0'+ new Date().getMinutes()

 }
	async function sendFecth(){
     const res = await fetch('http://localhost:5000', {
        method: 'POST',
          body: JSON.stringify({
    name: 'Dima',
    text: message,
    time: time,
}),
      headers: {
          'content-type': 'application/json'
      }
    })

     return res
     /*const body = await response.json();
        if (response.status !== 200) {
          throw Error(body.message)
        }*/
  }
  sendFecth().then();



  let arraytime= array;
  arraytime.push({
		    name: 'Dima',
		    text: message,
		    time: new Date().getHours() +':'+ new Date().getMinutes(),
		})
  setArrayDb(arraytime)
}


	return(
		<div className="inputDiv">
			<input onChange= {targetMessage}  type='text' />
			<div>
				<input type="radio" id="contactChoice1" name="contact" value="Bill" />
				<label for="contactChoice1">Bill</label>
				<input type="radio" id="contactChoice2" name="contact" value="Till" />
				<label for="contactChoice2">Till</label>
				<button onClick={hSend}></button>
			</div>
		</div>)
}

export default SendMessage;
