import React, { useState, useEffect } from 'react';

function SendMessage(props){
	const setArrayDb= props.setArrayDb;
	const array = props.arrayDb;
	const User = props.user
	const [message,setMessage]= useState('');
	function targetMessage(e){
		if(e.target.value !== '' || e.target.value !== ' '){
			setMessage(e.target.value);
		}

	}

function hSend(){
	let time
	 if(new Date().getMinutes() > 9){
	 	time = new Date().getHours() +':'+ new Date().getMinutes()
	 }else{
		time = new Date().getHours() +':0'+ new Date().getMinutes()
	 }
	async function sendFecth(){
     const res = await fetch('http://localhost:5000', {
        method: 'POST',
          body: JSON.stringify({
    name: User,
    text: message,
    time: time,
}),
      headers: {
          'content-type': 'application/json'
      }
    })

     return res
     /*const body = await response.json();
        if (response.status !== 200) {
          throw Error(body.message)
        }*/
  }
  sendFecth().then();



  let arraytime= array;
  arraytime.push({
		    name: 'Dima',
		    text: message,
		    time: new Date().getHours() +':'+ new Date().getMinutes(),
		})
  setArrayDb(arraytime)
}


	return(
		<div className="inputDiv">
			<input onChange= {targetMessage}  type='text' />
				<button className='inputDiv-btn' onClick={hSend}>Отправить</button>
		</div>)
}

export default SendMessage;
