import React, { useState, useEffect } from 'react';

function ArrayMessage(props){
	const arrayDb =  props.arrayDb
	const lists = arrayDb.map((item)=>
			<li className={'user'+ item.name} key={item._id}><p>{item.name}</p><span>{item.text}</span></li>

	  )
	useEffect(() => {
    let lastMessage = document.querySelector('ul li:last-child');
  lastMessage.scrollIntoView()
})
	//let lastMessage = document.querySelector('ul li:last-child');
	//lastMessage.scrollIntoView();
	return <ul>{lists}</ul>
}

export default ArrayMessage;
