import React, { useState, useEffect } from 'react';
import { useLayoutEffect, useRef } from "react";


function RegPage(props){
	const arrayAc =  props.arrayAc
	const User = props.user;
	const lists = arrayAc.map((item)=>
			<li className={item.name === User ? 'user' : 'user active'} key={item._id}>
				<div>
					<p className='nameMes'>{item.name}</p>
					<p className='textMes'>{item.text}</p>
					<p className='timeMes'>{item.time}</p>
				</div>
			</li>

	  )

	function handlerUser(e){
    setUser({user:e.target.textContent, reg:false})
  }

	return(
		<div className='user-change'>
	        <h2>Кто ты?</h2>
	        <ul>
	          <li onClick={handlerUser}>Дима</li>
	          <li onClick={handlerUser}>Даша</li>
	        </ul>
	    </div>
		)

}

export default RegPage;
