import React, { useState, useEffect } from 'react';

function ArrayMessage(props){
	const arrayDb =  props.arrayDb
	const lists = arrayDb.map((item)=>
			<li className={'user'+ item.name} key={item._id}>
				<p className='textMes'>{item.name}</p>
				<p className='textMes'>{item.text}</p>
				<p className='textMes'>{item.time}</p>
			</li>

	  )
	
	//let lastMessage = document.querySelector('ul li:last-child');
	//lastMessage.scrollIntoView();
	return <ul>{lists}</ul>
}

export default ArrayMessage;
